# withdraw : LP 빼기
# deposit : LP 추가
def pool_info():
    for name in pools : 
        print('\n===', name, '===')
#        if name != 'panda' :
#            continue
        ee = w3.eth.contract(pools[name]['addr'], abi=pools[name]['abi'])

        # get # of pools
        poolLength = ee.functions.poolLength().call()
        print('# of pools :', poolLength)
        print('-------------------------------------------')
        print("           pair       deposited      earned")
        print('-------------------------------------------')
        for i in range(1, poolLength) :
            amount = 0
            debt = 0
            if name == 'panda' :
                amount, debt = ee.functions.userInfo(i, my_addr).call()
            else :
                user_info = ee.functions.userInfo(i, my_addr).call()
                amount = user_info[0]

            if amount > 0 :  # for only staked pools
                pool_addr =''
                # get lp pair info
                if name == 'panda' :
                    pool_addr, allocPoint, lastRewardBlock, accCakePerShare  = ee.functions.poolInfo(i).call()
                else :
                    poolInfo = ee.functions.poolInfo(i).call()
                    pool_addr = poolInfo[0]
                # get pending cakes
                pending_cakes_real = 0
                deposit = 0
                if amount > 0 :
                    if name == 'chik' :
                        pending_cakes = ee.functions.pendingReward(i, my_addr).call() 
                    else :
                        pending_cakes = ee.functions.pendingCake(i, my_addr).call() 

                    pending_cakes_real = pending_cakes/(10 ** 18 )

                    # get deposited num lps 
                    deposit = amount / (10 ** 18 ) 

                # token info in lp
                addr1 = pool_addr
                e1 = w3.eth.contract(addr1, abi=contract_common_abi)
                # token 1
                sym1 = ''
                sym2 = ''
                token1 = e1.functions.token0().call()
                if token1 != None :
                    t1 = w3.eth.contract(token1, abi=contract_common_abi)
                    #print('token1', token1)
                    name1 = t1.functions.name().call()
                    sym1 = t1.functions.symbol().call()

                    # token 2
                    token2 = e1.functions.token1().call()
                    #print('token2', token2)
                    t2 = w3.eth.contract(token2, abi=contract_common_abi)
                    name2 = t2.functions.name().call()
                    sym2 = t2.functions.symbol().call()

                print("%2d [%6s - %6s] %10.3f   %10.3f"%(i, sym1, sym2, deposit, pending_cakes_real))
#                print('lp', addr1, '\n')
#                print(user_info[0])
            
    print('')

while True:
    try:
        pool_info()
        break
    except Exception as e:
        print(e)
        pass
